CREATE TABLE "UG_USERS" (
"USER_ID" NUMBER(10) GENERATED BY DEFAULT ON NULL AS IDENTITY,
"USERNAME" VARCHAR2(50) NOT NULL ,
"PASSWORD" VARCHAR2(200) NOT NULL ,
"AUTHORISED" CHAR NOT NULL ,
"ROLE_ID" NUMBER(10) NOT NULL ,
"DISPLAY_NAME" VARCHAR2(50) NOT NULL ,
"FIRST_NAME" VARCHAR2(30) NOT NULL ,
"LAST_NAME" VARCHAR2(30) NOT NULL ,
"EMAIL" VARCHAR2(100) NOT NULL ,
"PHONE_NUMBER" VARCHAR2(15) ,
"DOB" DATE ,
"COUNTRY_ID" NUMBER(10) ,
"LANGUAGE_ID" NUMBER(10) ,
"IP_ADDRESS" VARCHAR2(20) ,
"ACCOUNT_STATUS" VARCHAR2(20) NOT NULL ,
"CREATED_ON" DATE NOT NULL,
"CREATED_BY" NUMBER(10) NOT NULL ,
"UPDATED_ON" DATE ,
"UPDATED_BY" NUMBER(10) ,
"DELETE_FLAG" CHAR ,
constraint  "UG_USERS_PK" primary key ("USER_ID")
);
